for (const entry of ENTRIES) {
  const { key } = entry;
  const fallback = entry.en ?? key;
  for (const [lang, dict] of Object.entries(LANGUAGE_MAP)) {
    dict[key] = entry[lang] ?? fallback;
  }
}

